CREATE TABLE `book` (
  `book_id` varchar(32) NOT NULL COMMENT '图书主键',
  `book_name` varchar(100) DEFAULT NULL COMMENT '图书名称',
  `book_title` varchar(500) DEFAULT NULL COMMENT '图书标题',
  `book_author` varchar(50) DEFAULT NULL COMMENT '图书作者',
  `book_author_summary` text COMMENT '作者简介',
  `book_translator` varchar(50) DEFAULT NULL COMMENT '图书译者',
  `book_translator_summary` text COMMENT '译者简介',
  `book_catalog` text COMMENT '图书目录',
  `book_content_summary` text COMMENT '内容简介',
  `book_publish_country` varchar(50) DEFAULT NULL COMMENT '出版国家',
  `book_press` varchar(50) DEFAULT NULL COMMENT '出版社',
  `book_publish_date` date DEFAULT NULL COMMENT '出版日期',
  `book_cover` varchar(32) DEFAULT NULL COMMENT '图书封面',
  `book_priority` varchar(10) DEFAULT NULL COMMENT '优先级',
  `book_create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `book_update_time` datetime DEFAULT NULL COMMENT '修改时间',
  `book_sort` int(11) DEFAULT NULL COMMENT '图书排序',
  `book_state` tinyint(1) DEFAULT NULL COMMENT '图书状态',
  `book_no` varchar(100) DEFAULT NULL COMMENT '图书编号',
  `book_language` varchar(100) DEFAULT NULL COMMENT '图书语种',
  `foreign_name` varchar(100) DEFAULT NULL COMMENT '外文名称',
  `book_edition_order` varchar(100) DEFAULT NULL COMMENT '图书版次',
  `book_pages` int(11) DEFAULT NULL COMMENT '图书页数',
  `book_price` float DEFAULT NULL COMMENT '图书定价',
  `book_product_feature` varchar(32) DEFAULT NULL COMMENT '产品特色',
  `book_editor_recommend` text COMMENT '编辑推荐',
  `book_page_illustration` varchar(500) DEFAULT NULL COMMENT '内页插图',
  `book_wonderful_review` text COMMENT '精彩书评',
  `book_wonderful_digest` text COMMENT '精彩书摘',
  `book_preface` text COMMENT '前言序言',
  PRIMARY KEY (`book_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
